<body>
<div class="col-lg-6">
    <!--begin::Summary-->
<div class="card card-flush h-lg-100">
<!--begin::Card header-->
<div class="card-header mt-6">
    <!--begin::Card title-->
    <div class="card-title flex-column">
        <h3 class="fw-bold mb-1">Tasks Summary</h3>

        <div class="fs-6 fw-semibold text-gray-500">24 Overdue Tasks</div>
    </div>
    <!--end::Card title-->

    <!--begin::Card toolbar-->
    <div class="card-toolbar">
        <a href="#" class="btn btn-light btn-sm">View Tasks</a>
    </div>
    <!--end::Card toolbar-->
</div>
<!--end::Card header-->

<!--begin::Card body-->
<div class="card-body p-9 pt-5">
    <!--begin::Wrapper-->
    <div class="d-flex flex-wrap">
        <!--begin::Chart-->
        <div class="position-relative d-flex flex-center h-175px w-175px me-15 mb-7">
            <div class="position-absolute translate-middle start-50 top-50 d-flex flex-column flex-center">
                <span class="fs-2qx fw-bold">237</span>
                <span class="fs-6 fw-semibold text-gray-500">Total Tasks</span>
            </div>
            <canvas id="task-donut-chart" width="200" height="200"></canvas>
            {{-- <canvas id="project_overview_chart" width="262" height="262" style="display: block; box-sizing: border-box; height: 175px; width: 175px;"></canvas> --}}
        </div>
        <!--end::Chart-->

        <!--begin::Labels-->
        <div class="d-flex flex-column justify-content-center flex-row-fluid pe-11 mb-5">
            <!--begin::Label-->
            <div class="d-flex fs-6 fw-semibold align-items-center mb-3">
                <div class="bullet bg-primary me-3"></div>
                <div class="text-gray-500">Active</div>
                <div class="ms-auto fw-bold text-gray-700">30</div>
            </div>
            <!--end::Label-->

            <!--begin::Label-->
            <div class="d-flex fs-6 fw-semibold align-items-center mb-3">
                <div class="bullet bg-success me-3"></div>
                <div class="text-gray-500">Completed</div>
                <div class="ms-auto fw-bold text-gray-700">45</div>
            </div>
            <!--end::Label-->

            <!--begin::Label-->
            <div class="d-flex fs-6 fw-semibold align-items-center mb-3">
                <div class="bullet bg-danger me-3"></div>
                <div class="text-gray-500">Overdue</div>
                <div class="ms-auto fw-bold text-gray-700">0</div>
            </div>
            <!--end::Label-->

            <!--begin::Label-->
            <div class="d-flex fs-6 fw-semibold align-items-center">
                <div class="bullet bg-gray-300 me-3"></div>
                <div class="text-gray-500">Yet to start</div>
                <div class="ms-auto fw-bold text-gray-700">25</div>
            </div>
            <!--end::Label-->
        </div>
        <!--end::Labels-->
    </div>
    <!--end::Wrapper-->

</div>
<!--end::Card body-->
</div>
<!--end::Summary-->    </div>
<script>
 const ctx = document.getElementById('task-donut-chart').getContext('2d');
 new Chart(ctx, {
     type: 'doughnut',
     data: {
         datasets: [{
             data: [50, 45, 60, 42, 40],  // 5 nilai berbeda
             backgroundColor: [
                 '#4CAF50',  // Hijau
                 '#2196F3',  // Biru
                 '#FFC107',  // Kuning
                 '#9C27B0',  // Ungu
                 '#FF5722'   // Oranye
             ],
             borderWidth: 0,
             cutout: '75%'  // Membuat donut lebih tipis
         }]
     },
     options: {
         responsive: true,
         plugins: {
             legend: {
                 display: false
             }
         },
         // Menambahkan total di tengah
         plugins: [{
             id: 'centerText',
             beforeDraw: function(chart) {
                 const width = chart.width;
                 const height = chart.height;
                 const ctx = chart.ctx;

                 ctx.restore();
                 ctx.font = 'bold 24px Arial';
                 ctx.textBaseline = 'middle';
                 ctx.textAlign = 'center';

                 // Menghitung total dari semua data
                 const total = chart.data.datasets[0].data.reduce((a, b) => a + b, 0);

                 // Angka total
                 ctx.fillText(total, width/2, height/2 - 10);

                 // Text "Total Tasks"
                 ctx.font = '14px Arial';
                 ctx.fillStyle = '#666';
                 ctx.fillText('Total Tasks', width/2, height/2 + 15);

                 ctx.save();
             }
         }]
     }
 });
</script>


</body>
